        visited = [False] * len(p)
        for i, v in enumerate(p):
            if not visited[i]:
                cycle_length = 0
                while not visited[i]:
                    visited[i] = True
                    i = p[i] - 1
                    cycle_length += 1
                return cycle_length
        return 0

