        if parent[x] == x:
            return x
        parent[x] = find(parent, parent[x])
        return parent[x]

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1

    def kruskal(edges):
        parent = [i for i in range(n + 1)]
        rank = [0] * (n + 1)
        result = 0

        for edge in edges:
            u, v = edge
            if find(parent, u) != find(parent, v):
                union(parent, rank, u, v)
                result += 1

        return result

