    # Parse the input
    N, M = map(int, stdin.readline().split())
    edges = [tuple(map(int, stdin.readline().split())) for _ in range(M)]

    def find_bridges(edges: List[Tuple[int]]) -> int:
        # Build the graph
        graph = [[] for _ in range(N)]
        for u, v in edges:
            graph[u - 1].append(v - 1)
            graph[v - 1].append(u - 1)

        # Initialize the visited and low arrays
        visited = [False] * N
        low = [float('inf')] * N
        bridges = []

        def dfs(node: int, parent: int, depth: int) -> int:
            visited[node] = True
            low[node] = depth
            children = 0

            for neighbor in graph[node]:
                if neighbor == parent:
                    continue

                if not visited[neighbor]:
                    children += 1
                    dfs(neighbor, node, depth + 1)
                    low[node] = min(low[node], low[neighbor])

                    if low[neighbor] == depth and node != 0:
