def solution(stdin: str) -> str:
    def check_odd_sum(arr, r):
        for i in range(len(r) - 1):
            if sum(arr[r[i]:r[i + 1]]) % 2 == 0:
                return False
        return True

    def find_division(arr, n, k):
        if k == 1:
            if sum(arr) % 2 == 1:
                return [n]
            else:
                return None

        left, right = 0, n
        while left <= right:
            mid = (left + right) // 2
            if sum(arr[:mid]) % 2 == 1:
                left = mid + 1
            else:
                right = mid - 1

        if left == 0 or left >= n:
            return None

        r = [left]
        while len(r) < k - 1:
            r.append(left)
            while sum(arr[r[-1]:left]) % 2 == 0:
                left += 1
                if left >= n:
                    return None
            r[-1] = left

        r.append(n)
        if check_odd_sum(arr, r):
            return r
        else:
            return None

    q = int(stdin.readline())
    result = []
    for _ in range(q):
        n, k = map(int, stdin.readline().split())
        arr = list(map(int, stdin.readline().split()))
        division = find_division(arr, n, k)
        if division:
            result.append("YES")
            result.append(" ".join(map(str, division)))
        else:
            result.append("NO")

    return "\n".join(result)

