def solution(stdin: str) -> str:
    def move_left(grid: list) -> list:
        def merge(line: list) -> list:
            merged_line = []
            i = 0
            while i < len(line):
                if i == len(line) - 1 or line[i] != line[i + 1]:
                    merged_line.append(line[i])
                else:
                    merged_line.append(line[i] * 2)
                    i += 1
                i += 1
            return merged_line

        def move(line: list) -> list:
            line = [0 if x == 0 else x for x in line]
            return line[::-1]

        def pad(line: list) -> list:
            return line + [0] * (4 - len(line))

        moved_grid = []
        for line in grid:
            moved_line = move(line)
            merged_line = merge(moved_line)
            moved_grid.append(pad(merged_line))
        return moved_grid

    def move_up(grid: list) -> list:
        return transpose(move_left(transpose(grid)))

    def move_right(grid: list) -> list:
        return move_left(invert(grid))

    def move_down(grid: list) -> list:
        return invert(move_up(invert(grid)))

    def invert(grid: list) -> list:
        return [line[::-1] for line in grid]

    def transpose(grid: list) -> list:
        return [[grid[j][i] for j in range(4)] for i in range(4)]

    lines = stdin.strip().split('\n')
    grid = [[int(x) for x in line.strip().split(' ')] for line in lines[:4]]
    move_code = int(lines[4])

    if move_code == 0:
        moved_grid = move_left(grid)
    elif move_code == 1:
        moved_grid = move_up(grid)
    elif move_code == 2:
        moved_grid = move_right(grid)
    else:
        moved_grid = move_down(grid)

    return '\n'.join([' '.join([str(x) for x in line]) for line in moved_grid]) + '\n'

