    class Node:
        def __init__(self, value):
            self.value = value
            self.next = None

    class LinkedList:
        def __init__(self, head):
            self.head = head

        def has_cycle(self):
            slow = self.head
            fast = self.head

            while fast and fast.next:
                slow = slow.next
                fast = fast.next.next

                if slow == fast:
                    return True

            return False

    nodes = [Node(value) for value in head]
    for i in range(len(nodes) - 1):
        nodes[i].next = nodes[i + 1]

    if pos != -1:
        tail = nodes[pos]
        while tail.next:
            tail = tail.next
        tail.next = nodes[pos]

    linked_list = LinkedList(nodes[0])
    return str(linked_list.has_cycle()).lower()

