def solution(stdin: str) -> str:
    M, N = map(int, stdin.readline().split())
    vault = [list(map(int, stdin.readline().split())) for _ in range(M)]

    def is_reachable(height):
        visited = [[False] * N for _ in range(M)]
        queue = [(0, 0, vault[0][0])]
        visited[0][0] = True

        while queue:
            x, y, curr_height = queue.pop(0)

            if x == M - 1 and y == N - 1:
                return True

            for dx, dy in [(-1, 0), (0, -1), (1, 0), (0, 1)]:
                nx, ny = x + dx, y + dy

                if 0 <= nx < M and 0 <= ny < N and not visited[nx][ny] and abs(curr_height - vault[nx][ny]) <= height:
                    visited[nx][ny] = True
                    queue.append((nx, ny, vault[nx][ny]))

        return False

    left, right = 0, vault[-1][-1] + 1

    while left < right:
        mid = (left + right) // 2

        if is_reachable(mid):
            right = mid
        else:
            left = mid + 1

    return str(left - 1)

