        while left < right:
            mid = (left + right) // 2
            if sum_of_digits(mid) > X:
                right = mid - 1
            else:
                left = mid + 1
        if sum_of_digits(left) != X:
            raise ValueError("No valid N found")
        return left

    def binary_search_max(left: int, right: int) -> int:
        while left < right:
            mid = (left + right + 1) // 2
            if sum_of_digits(mid) > X:
                right = mid - 1
            else:
                left = mid
        if sum_of_digits(right) != X:
            raise ValueError("No valid M found")
        return right

    try:
        N = binary_search_min(L, D + 1)
        M = binary_search_max(L, D + 1)
