        silhouette = set()
        for row in range(len(frame)):
            for col in range(len(frame[0])):
                if frame[row][col] == C:
                    silhouette.add((row, col))
        return silhouette

    def extrapolate_silhouette(first_silhouette, second_silhouette, first_frame, second_frame):
        if not first_silhouette or not second_silhouette:
            return []

        def find_shift(first_silhouette, second_silhouette):
            for p1 in first_silhouette:
                for p2 in second_silhouette:
                    shift = (p2[0] - p1[0], p2[1] - p1[1])
                    if shift[0] <= M and shift[1] <= N and shift[0] >= -M and shift[1] >= -N:
                        return shift
            return None

        def shift_silhouette(silhouette, shift):
            shifted_silhouette = set()
            for point in silhouette:
                shifted_point = (point[0] + shift[0], point[1] + shift[1])
                if 0 <= shifted_point[0] < len(first_frame) and 0 <= shifted_point[1] < len(first_frame[0]):
                    shifted_silhouette.add(shifted_point)
            return shifted_silhouette

        shift = find_shift(first_silhouette, second_silhouette)
        if shift is None:
            return []

        shifted_silhouette = shift_silhouette(second_silhouette, shift)
        result_frame = [row[:] for row in first_frame]

        for point in shifted_silhouette:
            result_frame[point[0]][point[1]] = C

