    black_pieces = black.split(',')
    white_pieces = white.split(',')

    board = [['.'] * 8 for _ in range(8)]

    def place_piece(piece, position):
        row, col = ord(position[0]) - ord('a'), int(position[1]) - 1
        board[row][col] = piece

    for piece in white_pieces:
        place_piece(' ', piece)

    for piece in black_pieces:
        place_piece(' ', piece)

    def format_row(row):
        return ''.join(['+' if cell == '.' else '|' for cell in row]) + '+\n'

    formatted_board = ''.join([format_row(row) for row in board])
    formatted_board = formatted_board[:-1] + '+\n'

    def find_piece(piece, row, col):
        if row < 0 or row >= 8 or col < 0 or col >= 8:
            return None

        if board[row][col] == piece:
            return chr(row + ord('a')) + str(col + 1)
        else:
            return find_piece(piece, row + 1, col) or \
                   find_piece(piece, row - 1, col) or \
                   find_piece(piece, row, col + 1) or \
                   find_piece(piece, row, col - 1)

    def place_white_pieces(piece, symbol):
        for i in range(8):
            for j in range(8):
                if board[i][j] == '.':
                    position = find_piece(symbol, i, j)
                    if position:
                        board[i][j] = piece
                        white_pieces.remove(position)
                        return True
        return False

    def place_black_pieces(piece, symbol):
        for i in range(8):
            for j in range(8):
                if board[i][j] == '.':
                    position = find_piece(symbol, i, j)
                    if position:
                        board[i][j] = piece
                        black_pieces.remove(position)
                        return True
        return False

    while white_pieces:
        if not place_white_pieces('P', 'p'):
            if not place_white_pieces('N', 'n'):
                if not place_white_pieces('B', 'b'):
                    if not place_white_pieces('R', 'r'):
                        if not place_white_pieces('Q', 'q'):
                            if not place_white_pieces('K', 'k'):
                                break

    while black_pieces:
        if not place_black_pieces('P', 'p'):
            if not place_black_pieces('N', 'n'):
                if not place_black_pieces('B', 'b'):
                    if not place_black_pieces('R', 'r'):
                        if not place_black_pieces('Q', 'q'):
                            if not place_black_pieces('K', 'k'):
                                break

    formatted_board = formatted_board[:-1] + '+\n'
    return formatted_board + '\n'.join(white_pieces) + '\n' + '\n'.join(black_pieces)

