    def find_min_time(swathers, stages, time_matrix, completed, time):
        min_time = float('inf')
        for i, swather in enumerate(swathers):
            if completed[i] or time_matrix[i][0] + time != swather[0]:
                continue
            min_time = min(min_time, swather[0])
        return min_time

    def update_swathers(swathers, stages, time_matrix, completed, time):
        for i, swather in enumerate(swathers):
            if completed[i]:
                continue
            if time == swather[stages - 1]:
                completed[i] = True
            elif time >= swather[0] and time < swather[stages - 1]:
                for j in range(1, stages - 1):
                    if time == swather[j]:
                        swather[j] = 0

    def process_swathers(swathers, stages, time_matrix):
        completed = [False] * len(swathers)
        time = 0
        while not all(completed):
            min_time = find_min_time(swathers, stages, time_matrix, completed, time)
            time += min_time
            update_swathers(swathers, stages, time_matrix, completed, time)
        return [swather[-1] for swather in swathers]

    lines = stdin.strip().split('\n')
    N, M = map(int, lines[0].split())
    time_matrix = [list(map(int, lines[i + 1].split())) for i in range(N)]

