    n, s = map(int, stdin.readline().split())
    holes = []
    for _ in range(n):
        r, x, y, z = map(int, stdin.readline().split())
        holes.append((r, x, y, z))

    def weight(slice_thickness):
        total_weight = 0
        for r, x, y, z in holes:
            if z + r <= slice_thickness:
                total_weight += 4 / 3 * 3.14159265359 * r ** 3
            elif z - r >= slice_thickness:
                total_weight += 4 / 3 * 3.14159265359 * r ** 3
            else:
                dz = slice_thickness - z
                total_weight += 4 / 3 * 3.14159265359 * (r - dz) ** 3
        return total_weight

    def binary_search(lo, hi):
        while lo < hi:
            mid = (lo + hi) / 2
            if weight(mid) >= weight(mid + 1):
                hi = mid
            else:
                lo = mid + 1
        return lo

    slice_thicknesses = []
    for _ in range(s):
        lo = 0
        hi = 1000000
        slice_thickness = binary_search(lo, hi)
        slice_thicknesses.append(slice_thickness)

    return '\n'.join(f'{thickness:.10f}' for thickness in slice_thicknesses)

