    def is_valid(row, col):
        return 1 <= row <= N and 1 <= col <= N

    def bfs(board, start_row, start_col):
        queue = [(start_row, start_col, 0)]
        visited = {(start_row, start_col)}
        while queue:
            row, col, depth = queue.pop(0)
            if (row, col) == (1, 1):
                return depth
            for dr, dc in [(-2, 1), (-2, -1), (2, 1), (2, -1), (-1, 2), (-1, -2), (1, 2), (1, -2)]:
                new_row, new_col = row + dr, col + dc
                if is_valid(new_row, new_col) and board[new_row - 1][new_col - 1] != '#' and (new_row, new_col) not in visited:
                    visited.add((new_row, new_col))
                    queue.append((new_row, new_col, depth + 1))
        return -1

    N = int(stdin.readline().strip())
    board = [list(stdin.readline().strip()) for _ in range(N)]
    knight_pos = None
    for row in range(N):
        for col in range(N):
            if board[row][col] == 'K':
                knight_pos = (row + 1, col + 1)
                break
        if knight_pos:
            break

    result = bfs(board, knight_pos[0], knight_pos[1])
    return str(result)

