def solution(stdin: str) -> str:
    # Read the key phrase and plaintext
    key_phrase, plaintext = stdin.strip().split('\n')

    # Create the Playfair encryption key
    key = ''.join(sorted(set(key_phrase.replace(' ', ''))))
    key = key.upper()
    key = key.replace('Q', '')

    # Fill the Playfair table
    table = []
    row = []
    for char in key:
        row.append(char)
        if len(row) == 5:
            table.append(row)
            row = []
    if row:
        table.append(row)

    # Add the rest of the alphabet to the table
    for char in 'ABCDEFGHIJKLMNOPSTUVWXYZ':
        if char not in key:
            for i, row in enumerate(table):
                if len(row) < 5:
                    row.append(char)
                    break
            if len(table) == i + 1 and len(table[i]) == 5:
                table.append([char])

    # Encrypt the plaintext
    encrypted = []
    plaintext = plaintext.upper()
    i = 0
    while i < len(plaintext):
        char1 = plaintext[i]
        char2 = plaintext[i + 1] if i + 1 < len(plaintext) else 'X'
        i += 2

        if char1 == char2:
            encrypted.append(char1)
            char2 = 'X'
        else:
            for row in table:
                if char1 in row and char2 in row:
                    encrypted.append(table[row.index(char1) + 1][row.index(char2)])
                    break
                elif char1 in row:
                    encrypted.append(table[row.index(char1)][table.index(row)])
                    break
                elif char2 in row:
                    encrypted.append(table[row.index(char2)][table.index(row) - 1])
                    break
            else:
                encrypted.append(char1)

    return ''.join(encrypted)

