def solution(stdin: str) -> str:
    def average_cost(total_cost, total_shares, new_shares):
        if total_shares < new_shares:
            return total_cost
        else:
            return (total_cost + total_cost * (new_shares - total_shares)) / total_shares

    lines = stdin.split('\n')
    shares = 0
    total_cost = 0
    taxes = 0

    for line in lines:
        command, *args = line.split()
        if command == 'buy':
            x, y = map(int, args)
            shares += x
            total_cost += x * y
        elif command == 'sell':
            x, y = map(int, args)
            profit = (y - average_cost(total_cost, shares, x)) * x
            if profit > 0:
                taxes += profit * 0.3
            shares -= x
            total_cost -= x * average_cost(total_cost, shares + x, x)
        elif command == 'split':
            x = int(args[0])
            total_cost = total_cost / shares * (shares * x)
            shares *= x
        elif command == 'merge':
            x = int(args[0])
            total_cost = total_cost / shares * (shares // x + shares % x)
            shares = (shares // x) * (shares % x)
        elif command == 'die':
            y = int(args[0])
            profit = (y - average_cost(total_cost, shares, shares)) * shares
            taxes += profit * 0.3
            total_cost += shares * y

    return round(total_cost - taxes, 9)

