            if is_safe(rooms, order):
                return " ".join(map(str, order))
            else:
                return "impossible"

        for i in range(len(rooms)):
            if i + 1 in remaining_rooms:
                new_order = order + [i + 1]
                new_remaining_rooms = remaining_rooms.copy()
                new_remaining_rooms.remove(i + 1)
                result = find_safe_order(rooms, new_remaining_rooms, new_order)
                if result != "impossible":
                    return result

