def solution(stdin: str) -> str:
    A, B, C, D = map(int, stdin.split())

    def gcd(a, b):
        while b:
            a, b = b, a % b
        return a

    def lcm(a, b):
        return a * b // gcd(a, b)

    def largest_rectangle(width, height):
        return width * height

    def enclose_largest_rectangle(A, B, C, D):
        area = 0
        for a in [A, B, C, D]:
            for b in [A, B, C, D]:
                if a == b:
                    continue
                for c in [A, B, C, D]:
                    if c in [a, b]:
                        continue
                    for d in [A, B, C, D]:
                        if d in [a, b, c]:
                            continue
                        l = lcm(a, lcm(b, lcm(c, d)))
                        area = max(area, largest_rectangle(l, l // a) * 4)
                        area = max(area, largest_rectangle(l, l // b) * 4)
                        area = max(area, largest_rectangle(l, l // c) * 4)
                        area = max(area, largest_rectangle(l, l // d) * 4)
        return area

    return str(enclose_largest_rectangle(A, B, C, D))

