    def find_max_increasing_sequence(arr):
        if len(arr) == 0:
            return 0
        if len(arr) == 1:
            return 1

        left, right = 0, len(arr) - 1
        max_length = 1
        while left < right:
            if arr[left] < arr[left + 1]:
                left += 1
                max_length += 1
            elif arr[right] < arr[right - 1]:
                right -= 1
                max_length += 1
            else:
                break
        return max_length

