    max_length = find_max_increasing_sequence(arr)

    result = ""
    left, right = 0, len(arr) - 1
    for _ in range(max_length):
        if arr[left] < arr[right]:
            result += "L"
            left += 1
        else:
            result += "R"
            right -= 1

    return f"{max_length}\n{result}"

