    n, f = list(map(int, stdin.split()))

    # Find the friend who gives the gift to the friend with index 0
    for i in range(n):
        if f[i] == 0:
            continue
        elif f[f[i] - 1] == i + 1:
            break

    # Swap the values at index i and f[i]
    f[i], f[f[i] - 1] = f[f[i] - 1], f[i]

    # Find the remaining unknown values
    for i in range(n):
        if f[i] == 0:
            for j in range(n):
                if j == i or j == f[i] - 1:
                    continue
                if f[j] == 0 or f[j] == i + 1:
                    f[j], f[i] = f[i], f[j]
                    break

    # Check if there are any unfilled values
    for i in range(n):
        if f[i] == 0:
            raise ValueError("No valid solution exists")

    return " ".join(map(str, f))

