        for i in range(len(exams) - 1):
            if exams[i][1] > exams[i][0] + exams[i][2]:
                return False
            if exams[i][1] + 1 == exams[i + 1][1] and exams[i][1] - exams[i][0] > exams[i + 1][0] - exams[i + 1][1]:
                return False
        return True

    def generate_schedule(schedule: List[Tuple[int, int, int]], n: int) -> List[int]:
        exams = sorted(schedule, key=lambda x: x[1])
        rest_days = n - sum(x[2] for x in exams) - len(exams)
        schedule = [0] * n
        for exam in exams:
            schedule[exam[1]] = exam[0]
            for i in range(exam[0], exam[1]):
                if schedule[i] == 0:
                    schedule[i] = exam[0]
            for i in range(exam[1] + 1, exam[1] + exam[2] + 1):
