def solution(stdin: str) -> str:
    n, k = map(int, stdin.split())
    s = input()

    def is_subsequence(t: str, s: str) -> bool:
        t = iter(t)
        return all(c in t for c in s)

    def subsequence_cost(t: str, s: str) -> int:
        return n - len(t)

    def min_cost_to_generate_set(s: str, k: int) -> int:
        if k == 0:
            return 0

        unique_subsequences = set()
        min_cost = float("inf")

        for i in range(len(s) + 1):
            for j in range(i, len(s) + 1):
                t = s[i:j]
                if is_subsequence(t, s) and t not in unique_subsequences:
                    unique_subsequences.add(t)
                    if len(unique_subsequences) == k:
                        min_cost = min(min_cost, subsequence_cost(t, s))
                    else:
                        cost = subsequence_cost(t, s)
                        remaining_cost = min_cost_to_generate_set(s.replace(t, ""), k - len(unique_subsequences))
                        if remaining_cost != -1:
                            min_cost = min(min_cost, cost + remaining_cost)

        return min_cost if min_cost != float("inf") else -1

    result = min_cost_to_generate_set(s, k)
    return str(result)

