    def is_valid_spanning_tree(edges, n, m, D):
        graph = [[] for _ in range(n + 1)]
        for edge in edges:
            v, u = edge
            graph[v].append(u)
            graph[u].append(v)

        visited = [False] * (n + 1)
        stack = [(1, 0)]
        visited[1] = True
        degree_1 = 0
        spanning_tree_edges = []

        while stack:
            v, parent = stack.pop()
            for u in graph[v]:
                if not visited[u]:
                    visited[u] = True
                    stack.append((u, v))
                    if u == 1:
                        degree_1 += 1
                        spanning_tree_edges.append((parent, u))
                    else:
                        spanning_tree_edges.append((v, u))

        return degree_1 == D and len(spanning_tree_edges) == n - 1

    def find_spanning_tree(edges, n, m, D):
        for i in range(m):
            new_edges = edges.copy()
            new_edges.pop(i)
