    modulo = 1000000007

    def count_regular_bracket_sequences(n, s):
        dp = [0] * (len(s) + 1)
        dp[0] = 1

        for i in range(1, 2 * n + 1):
            for j in range(i - 1, -1, -1):
                if s[j] == '(' and s[i - j - 1] == ')':
                    dp[i] += dp[j] + dp[i - j - 1]

        return dp[len(s)] % modulo

