    def is_subsequence(s: str, t: str) -> bool:
        if not t:
            return True
        i = 0
        for c in s:
            if c == t[i]:
                i += 1
                if i == len(t):
                    return True
        return False

    def lcp(s: str, t: str) -> int:
        n, m = len(s), len(t)
        dp = [[0] * (m + 1) for _ in range(n + 1)]
        res = 0
        for i in range(n):
            for j in range(m):
                if s[i] == t[j]:
                    dp[i + 1][j + 1] = dp[i][j] + 1
                    res = max(res, dp[i + 1][j + 1])
        return res

    def find_max_length(s: str, t: str) -> int:
        if is_subsequence(s, t):
            return max(len(s) - len(t), 0)
        l = lcp(s, t)
        return find_max_length(s[l:], t[l:]) + l

    return find_max_length(s, t)

