    def is_valid_game(sequence):
        for i in range(1, len(sequence)):
            if sequence[i] == sequence[i - 1] * 2:
                continue
            elif sequence[i] == sequence[i - 1] // 3:
                continue
            else:
                return False
        return True

    def rearrange(sequence):
        for i in range(n):
            if sequence[i] % 3 == 0:
                temp = sequence[i] // 3
                if temp in sequence:
                    sequence.pop(i)
                    sequence.insert(sequence.index(temp), temp)
                    if is_valid_game(sequence):
                        return " ".join(list(map(str, sequence)))
                    else:
                        sequence.pop(sequence.index(temp))
                        sequence.insert(i, temp)
        return "No solution found"

    return rearrange(sequence)

