        for i in range(len(points)):
            for j in range(i + 1, len(points)):
                if abs(points[i] - points[j]) == 1:
                    return False
        return True

    def binary_search(points: List[int], start: int, end: int) -> int:
        while start <= end:
            mid = (start + end) // 2
            if is_valid(points[:mid]):
                start = mid + 1
            else:
                end = mid - 1
        return end

