    H, W, K = map(int, stdin.split()[:3])
    grid = [list(row) for row in stdin.split()[3:]]

    def count_black_squares(rows: set, cols: set) -> int:
        black_squares = 0
        for i in rows:
            for j in cols:
                if grid[i - 1][j - 1] == '#':
                    black_squares += 1
        return black_squares

    total_black_squares = sum(row.count('#') for row in grid)
    total_white_squares = H * W - total_black_squares

    if K > total_black_squares or K > total_white_squares:
        return '0'

    combinations = 0
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                continue
            black_squares = total_black_squares - count_black_squares({i + 1}, {j + 1})
            white_squares = total_white_squares - (H - 1) * (W - 1) + black_squares
            if white_squares + black_squares - K == 0:
                combinations += 1

    return str(combinations)

