def solution(stdin: str) -> str:
    def dfs(v, parent, graph, visited):
        for u in graph[v]:
            if u == parent:
                continue
            if u in visited:
                return False
            visited.add(u)
            if not dfs(u, v, graph, visited):
                return False
        return True

    def check_for_cycles(v, parent, graph, visited):
        visited.add(v)
        for u in graph[v]:
            if u == parent:
                continue
            if u in visited:
                return True
            if check_for_cycles(u, v, graph, visited):
                return True
        return False

    def create_graph(n, edges):
        graph = [set() for _ in range(n)]
        for u, v in edges:
            graph[u - 1].add(v - 1)
            graph[v - 1].add(u - 1)
        return graph

    def convert_edges(directed_edges):
        return ''.join('1' if u < v else '0' for u, v in directed_edges)

    input_data = [line.strip() for line in stdin.split('\n')]
    n, m = map(int, input_data[0].split())
    edges = [tuple(map(int, line.split())) for line in input_data[1:]]

    graph = create_graph(n, edges)
    visited = set()
    if not dfs(0, -1, graph, visited) or check_for_cycles(0, -1, graph, set()):
        return "NO"

    directed_edges = [(u, v) if u < v else (v, u) for u, v in edges]
    return "YES\n" + convert_edges(directed_edges)

