def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    a = list(map(int, stdin.split()[2:n + 2]))
    special_offers = []
    for i in range(m):
        x, y, w = map(int, stdin.split()[n + 2 + i * 3:n + 2 + (i + 1) * 3])
        special_offers.append([x - 1, y - 1, w])

    def kruskal(n, a, edges):
        parent = [-1] * n
        rank = [0] * n
        edges = sorted(edges, key=lambda x: x[2])

        def find(x):
            if parent[x] == -1:
                return x
            parent[x] = find(parent[x])
            return parent[x]

        def union(x, y):
            x_root = find(x)
            y_root = find(y)
            if x_root == y_root:
                return False
            if rank[x_root] > rank[y_root]:
                parent[y_root] = x_root
            else:
                parent[x_root] = y_root
                if rank[x_root] == rank[y_root]:
                    rank[y_root] += 1
            return True

        added_edges = []
        for x, y, w in edges:
            if union(x, y):
                added_edges.append([x, y, w])

        return added_edges

    edges = [[i, j, a[i] + a[j]] for i in range(n) for j in range(i + 1, n)]
    special_offers = [[i, j, w] for i, j, w in special_offers if i < j]
    edges += special_offers

    added_edges = kruskal(n, a, edges)
    min_cost = sum(w for i, j, w in added_edges)

    return str(min_cost)

