    adj_list = [[] for _ in range(n + 1)]

    for u, v in edges:
        adj_list[u].append(v)
        adj_list[v].append(u)

    parent = [-1] * (n + 1)
    depth = [0] * (n + 1)

    def dfs(node, depth_level):
        for neighbor in adj_list[node]:
            if neighbor != parent[node]:
                parent[neighbor] = node
                depth[neighbor] = depth_level + 1
                dfs(neighbor, depth_level + 1)

    dfs(1, 0)
    diameter = max(depth)

    if diameter == n - 1:
        return f"{diameter}\n"

    missing_edges = []
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            if parent[i] != j and parent[j] != i:
                missing_edges.append((i, j))

    added_edges = []
    for edge in missing_edges:
        parent_i, parent_j = parent[edge[0]], parent[edge[1]]
        while parent_i != parent_j:
            if depth[parent_i] < depth[parent_j]:
                parent_i, parent_j = parent[parent_i], parent_j
            else:
                parent_i, parent_j = parent_i, parent[parent_j]
        if parent_i == parent_j:
            added_edges.append(edge)

    result = [f"{diameter}"]
    for edge in added_edges:
        result.append(f"{edge[0]} {edge[1]}")

    return "\n".join(result) + "\n"

