def solution(stdin: str) -> str:
    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if xroot == yroot:
            return

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1

    def kruskal(n, edges):
        parent = [i for i in range(n)]
        rank = [0] * n
        mst_cost = 0
        mst_edges = []

        edges.sort(key=lambda e: e[2])

        for edge in edges:
            u, v, w = edge
            if find(parent, u) != find(parent, v):
                union(parent, rank, u, v)
                mst_cost += w
                mst_edges.append(edge)

        return mst_cost, mst_edges

    def kruskal_unique(n, edges):
        parent = [i for i in range(n)]
        rank = [0] * n
        mst_cost = 0
        mst_edges = []

        edges.sort(key=lambda e: (-e[2], e[0], e[1]))

        for edge in edges:
            u, v, w = edge
            if find(parent, u) != find(parent, v):
                union(parent, rank, u, v)
                mst_cost += w
                mst_edges.append(edge)
                if len(mst_edges) == n - 1:
                    break

        return mst_cost, mst_edges

    def process(n, edges):
        initial_cost, mst_edges = kruskal(n, edges)
        unique_cost, unique_mst_edges = kruskal_unique(n, edges)

        if initial_cost != unique_cost:
            return -1

        operations = 0
        for edge in mst_edges:
            if edge not in unique_mst_edges:
                operations += 1

        return operations

    stdin_list = stdin.strip().split('\n')
    n, m = map(int, stdin_list[0].split())
    edges = [tuple(map(int, line.split())) for line in stdin_list[1:]]

    result = process(n, edges)
    return str(result) if result != -1 else "Incompatible trees."

