    def kruskal(n: int, edges: list) -> list:
        parent = [i for i in range(n)]
        rank = [0] * n

        def find(u: int) -> int:
            if u == parent[u]:
                return u
            parent[u] = find(parent[u])
            return parent[u]

        def union(u: int, v: int):
            u_root = find(u)
            v_root = find(v)
            if u_root == v_root:
                return False
            if rank[u_root] > rank[v_root]:
                parent[v_root] = u_root
            else:
                parent[u_root] = v_root
                if rank[u_root] == rank[v_root]:
                    rank[v_root] += 1
            return True

        mst = []
        for e in sorted(edges, key=lambda x: x[2]):
            if union(e[0], e[1]):
                mst.append(e)
                if len(mst) == n - 1:
                    break
        return mst

    lines = stdin.split('\n')
    n, m = map(int, lines[0].split())
    edges = []
    for i in range(1, m + 1):
        v, u = map(int, lines[i].split())
        edges.append((v - 1, u - 1, i))

    mst = kruskal(n, edges)
    result = [f'{v + 1} {u + 1}' for v, u, _ in mst]
    return '\n'.join(result)

