    edges = [tuple(map(int, line.split())) for line in stdin.split('\n')[1:]]

    # Create the graph
    graph = [[] for _ in range(n + 1)]
    for x, y, w in edges:
        graph[x].append((y, w))
        graph[y].append((x, w))

    # Initialize the shortest paths matrix
    shortest_paths = [[float('inf')] * (n + 1) for _ in range(n + 1)]
    for i in range(n + 1):
        shortest_paths[i][i] = 0

    # Dijkstra's algorithm
    vertices = {i: float('inf') for i in range(1, n + 1)}
    vertices[1] = 0
    while vertices:
        curr_vertex = min(vertices, key=vertices.get)
        curr_distance = vertices.pop(curr_vertex)
        for neighbor, weight in graph[curr_vertex]:
            distance = curr_distance + weight
            if distance < shortest_paths[curr_vertex][neighbor]:
                shortest_paths[curr_vertex][neighbor] = distance
                shortest_paths[neighbor][curr_vertex] = distance
                vertices[neighbor] = min(vertices.get(neighbor, float('inf')), distance)

    # Create the array of shortest paths and sort it
    shortest_paths_array = []
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            shortest_paths_array.append(shortest_paths[i][j])
    shortest_paths_array.sort()

    # Return the k-th smallest shortest path
    return str(shortest_paths_array[k - 1])

