def solution(stdin: str) -> str:
    n, m = map(int, stdin.split())

    mod = 10**9 + 7

    if n == 0 or m == 0:
        return str((n + m) * (n + m + 1) // 2 % mod)
    if n == 1 and m == 1:
        return str(2 % mod)
    if n == 1 or m == 1:
        return str((n + m) * (n + m + 1) // 2 % mod)

    total_animals = n + m
    total_spaces = total_animals - 1

    # All arrangements can be represented as a sequence of 1's and 0's, where 1 represents an animal and 0 represents a space
    # Therefore, there are 2^(total_spaces) possible arrangements
    total_arrangements = pow(2, total_spaces, mod)

    # However, some of these arrangements may have two adjacent dogs or two adjacent monkeys
    # To count the number of invalid arrangements, we can use the inclusion-exclusion principle
    # Specifically, let S be the set of all arrangements, and let S_i be the set of arrangements with two adjacent animals of the i-th type
    # Then we want |S| - |S_1 union S_2| + |S_1 intersection S_2| - ...

    # First, we count the number of arrangements with two adjacent dogs or two adjacent monkeys
    invalid_arrangements = 0
    for i in range(n - 1):
        # Case 1: Two adjacent dogs
        num_dogs = i + 1
        num_monkeys = m
        num_spaces = total_spaces - num_dogs - num_monkeys

        if num_spaces >= 0:
            invalid_arrangements += pow(2, num_spaces, mod)

        # Case 2: Two adjacent monkeys
        num_dogs = n
        num_monkeys = i + 1
        num_spaces = total_spaces - num_dogs - num_monkeys

        if num_spaces >= 0:
            invalid_arrangements += pow(2, num_spaces, mod)

    # Next, we subtract the number of invalid arrangements from the total number of arrangements
    total_arrangements -= invalid_arrangements

    # Finally, we account for the fact that some arrangements may be indistinguishable
    # Specifically, if we have arrangements A1A2 and A2A1, we only count one of them
    # To do this, we divide the total number of arrangements by the number of indistinguishable arrangements
    num_indistinguishable = 1
    for i in range(1, n + 1):
        num_indistinguishable *= i
    for i in range(1, m + 1):
        num_indistinguishable *= i
    for i in range(1, total_spaces + 1):
        num_indistinguishable //= i

    return str(total_arrangements * num_indistinguishable % mod)

