def solution(stdin: str) -> str:
    def binary_search(a, x):
        left, right = 0, len(a) - 1
        while left <= right:
            mid = (left + right) // 2
            if a[mid] == x:
                return mid
            elif a[mid] < x:
                left = mid + 1
            else:
                right = mid - 1
        return -1

    lines = stdin.splitlines()
    n, m, k = map(int, lines[0].split())
    a = list(map(int, lines[1].split()))
    special_offers = [tuple(map(int, line.split())) for line in lines[2:2 + m]]

    a.sort()

    def min_cost(shovels_left, offers_left, current_purchase_size):
        if shovels_left == 0 or current_purchase_size == 0:
            return 0

        if current_purchase_size > n:
            return float('inf')

        if offers_left and current_purchase_size == special_offers[offers_left - 1][0]:
            return a[current_purchase_size - 1] * special_offers[offers_left - 1][1] + min_cost(shovels_left - special_offers[offers_left - 1][1], offers_left - 1, current_purchase_size - special_offers[offers_left - 1][1])

        return a[current_purchase_size - 1] + min_cost(shovels_left, offers_left, current_purchase_size - 1)

    min_cost_result = float('inf')
    for i in range(1, k + 1):
        index = binary_search(a, a[i - 1] * i)
        if index != -1:
            min_cost_result = min(min_cost_result, a[i - 1] * i + min_cost(k - i, m, index - i + 1))

    return str(min_cost_result)

