    carry = 1
    for i in range(len(digits) - 1, -1, -1):
        digit = digits[i] + carry
        if digit > 9:
            digits[i] = digit % 10
            carry = 1
        else:
            digits[i] = digit
            carry = 0
    if carry == 1:
        digits.insert(0, carry)
    return " ".join(str(digit) for digit in digits)

