def solution(stdin: str) -> str:
    def is_valid(canvas: list, row: int, col: int, rows: int, cols: int) -> bool:
        if 0 <= row < rows and 0 <= col < cols:
            return True
        return False

    def dfs(canvas: list, row: int, col: int, rows: int, cols: int) -> bool:
        if canvas[row][col] == "#":
            return True

        if canvas[row][col] == ".":
            canvas[row][col] = "*"

            up = dfs(canvas, row - 1, col, rows, cols)
            down = dfs(canvas, row + 1, col, rows, cols)
            left = dfs(canvas, row, col - 1, rows, cols)
            right = dfs(canvas, row, col + 1, rows, cols)

            if up or down or left or right:
                return True

            canvas[row][col] = "."

        return False

    input_list = stdin.split("\n")
    H, W = map(int, input_list[0].split())

    canvas = []
    for i in range(1, H + 1):
        row = list(input_list[i])
        canvas.append(row)

    for i in range(H):
        for j in range(W):
            if canvas[i][j] == "#":
                if not is_valid(canvas, i - 1, j, H, W):
                    continue
                if not is_valid(canvas, i + 1, j, H, W):
                    continue
                if not is_valid(canvas, i, j - 1, H, W):
                    continue
                if not is_valid(canvas, i, j + 1, H, W):
                    continue

                if dfs(canvas, i - 1, j, H, W) or dfs(canvas, i + 1, j, H, W) or dfs(canvas, i, j - 1, H, W) or dfs(canvas, i, j + 1, H, W):
                    return "Yes"

    return "No"

