    P = [list(map(int, stdin.readline().strip().split())) for _ in range(N + 1)]

    def is_valid(c: List[int]) -> bool:
        for i in range(1, N + 1):
            if all(F[i - 1][j][k] != c[i] for j in range(5) for k in range(2)):
                return False
        return True

