def solution(stdin: str) -> str:
    def is_odd(num: int) -> bool:
        return num % 2 == 1

    def is_valid_segment_sum(segment_sum: int) -> bool:
        return is_odd(segment_sum)

    def is_valid_segment(segment: List[int]) -> bool:
        segment_sum = sum(segment)
        return is_valid_segment_sum(segment_sum)

    def find_valid_segment_sizes(a: List[int], n: int, k: int) -> List[int]:
        if n < k:
            return []

        valid_sizes = []
        current_size = n // k
        current_sum = sum(a[:current_size])

        if is_valid_segment_sum(current_sum):
            valid_sizes.append(current_size)

        for i in range(1, k):
            left_border = current_size * i
            right_border = current_size * (i + 1)
            current_sum -= sum(a[left_border:right_border - 1])
            current_sum += sum(a[right_border - 1:right_border + current_size - 1])

            if is_valid_segment_sum(current_sum):
                valid_sizes.append(current_size)
            else:
                return []

        return valid_sizes

    def split_array(a: List[int], n: int, k: int) -> List[List[int]]:
        segments = []
        start_index = 0

        for segment_size in find_valid_segment_sizes(a, n, k):
            segment = a[start_index:start_index + segment_size]
            segments.append(segment)
            start_index += segment_size

        return segments

    def check_segment_sums(segments: List[List[int]]) -> bool:
        for segment in segments:
            if not is_valid_segment(segment):
                return False
        return True

    queries = stdin.split("\n")
    total_n = 0
    for i in range(1, len(queries), 2):
        total_n += int(queries[i].split()[0])

    if total_n > 2 * 10**5:
        raise ValueError("The sum of n over all queries does not exceed 2 * 10^5")

    result = []
    for i in range(0, len(queries), 2):
        query = queries[i + 1].split()
        n, k = map(int, query[0:2])
        a = list(map(int, query[2:]))

        if len(a) != n:
            raise ValueError("Invalid input format")

        segments = split_array(a, n, k)

        if check_segment_sums(segments):
            result.append("YES")
            result.append(" ".join(map(str, [len(segment) for segment in segments])))
        else:
            result.append("NO")

    return "\n".join(result)

