    def move(grid: List[List[int]], direction: int) -> List[List[int]]:
        if direction == 0:
            grid = [row[::-1] for row in grid]
        grid = merge(grid)
        if direction == 0:
            grid = [row[::-1] for row in grid]
        grid = push(grid, direction)
        grid = merge(grid)
        return grid

    def merge(grid: List[List[int]]) -> List[List[int]]:
        for i in range(4):
            for j in range(3):
                if grid[i][j] == grid[i][j + 1] and grid[i][j] != 0:
                    grid[i][j] *= 2
                    grid[i][j + 1] = 0
        return grid

    def push(grid: List[List[int]], direction: int) -> List[List[int]]:
        if direction == 0:
            grid = [row[::-1] for row in grid]
        for i in range(4):
            j = 0
            while j < 3:
                if grid[i][j] == 0:
                    grid[i].pop(j)
                    grid[i].append(0)
                else:
                    j += 1
        if direction == 0:
            grid = [row[::-1] for row in grid]
        return grid

    lines = stdin.split('\n')
    grid = [list(map(int, line.split())) for line in lines[:-2]]
    direction = int(lines[-1])

    grid = move(grid, direction)
    return '\n'.join([' '.join(map(str, row)) for row in grid])

