def solution(stdin: str) -> str:
    # Split input into separate lines
    lines = stdin.split("\n")

    # Get length of program line and the program line itself
    length = int(lines[0])
    program = lines[1]

    # Initialize stack to keep track of opening delimiters
    stack = []

    # Iterate through each character in the program
    for i, char in enumerate(program):
        # If the character is an opening delimiter, push it onto the stack
        if char in ["(", "[", "{"]:
            stack.append(char)
        # If the character is a closing delimiter, check if it matches the last opening delimiter in the stack
        elif char in [")", "]", "}"]:
            # If the stack is empty or the last opening delimiter does not match the closing delimiter, return the character and its index
            if not stack or stack[-1] + char not in ["()", "[]", "{}"]:
                return char + " " + str(i)
            # If the delimiters match, pop the last opening delimiter from the stack
            else:
                stack.pop()

    # If there are no errors or more opening delimiters than closing delimiters, return 'ok so far'
    return "ok so far"

