        for i in range(0, len(stack), 2):
            if stack[i] != stack[i + 1]:
                return False
        return True

    def min_moves(n, socks):
        moves = 0
        stack = []
        aux_stack = []

        for i in range(2 * n):
            if not stack:
                stack.append(socks[i])
            else:
                top_sock = stack[-1]
                if top_sock == socks[i]:
                    moves += 1
                    stack.pop()
                elif not aux_stack or aux_stack[-1] != socks[i]:
                    aux_stack.append(socks[i])
                else:
                    moves += 2
                    aux_stack.pop()
                    stack.append(socks[i])

        while aux_stack:
            top_aux = aux_stack[-1]
            index = find_pair(stack, top_aux)
            if index != -1:
                moves += index
                stack.pop(index)
                aux_stack.pop()
            else:
                return "impossible"

        return moves

