    def bfs(matrix, start, end):
        q = [(start, 0)]
        visited = {start: 0}

        while q:
            current, ladder_length = q.pop(0)

            if current == end:
                return ladder_length

            for dx, dy in [(-1, 0), (0, -1), (1, 0), (0, 1)]:
                nx, ny = current[0] + dx, current[1] + dy
                if 0 <= nx < len(matrix) and 0 <= ny < len(matrix[0]) and (nx, ny) not in visited:
                    if matrix[nx][ny] <= ladder_length:
                        visited[(nx, ny)] = ladder_length
                        q.append(((nx, ny), ladder_length))
                    else:
                        visited[(nx, ny)] = ladder_length + matrix[nx][ny] - ladder_length
                        q.append(((nx, ny), ladder_length + matrix[nx][ny] - ladder_length))

        return -1

    m, n = map(int, stdin.readline().strip().split())
    matrix = [list(map(int, stdin.readline().strip().split())) for _ in range(m)]

    return str(bfs(matrix, (0, 0), (m - 1, n - 1)))

