    def heron_formula(a, b, c):
        s = (a + b + c) / 2
        return (s * (s - a) * (s - b) * (s - c)) ** 0.5

    max_area = 0

    # Cycle through all possible combinations of sides
    for i in range(4):
        side1, side2, side3, side4 = s1, s2, s3, s4
        # Rotate sides to consider all possible quadrilaterals
        for _ in range(i):
            side1, side2, side3, side4 = side2, side3, side4, side1
        # Calculate area of the quadrilateral with current sides
        area = abs(side1 * side2 * side3 * side4) / (heron_formula(side1, side2, side3) + heron_formula(side1, side2, side4) + heron_formula(side1, side3, side4) + heron_formula(side2, side3, side4))
        max_area = max(max_area, area)

    return format(max_area, ".6f")

