        return 0 <= x < N and 0 <= y < M and not grid[x][y]

    def bfs(start_x, start_y):
        queue = [(start_x, start_y)]
        visited = set()
        while queue:
            x, y = queue.pop(0)
            if (x, y) not in visited:
                if x == 0 or x == N - 1 or y == 0 or y == M - 1:
                    return True
                visited.add((x, y))
                for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                    nx, ny = x + dx, y + dy
                    if is_valid(nx, ny) and (dx, dy) not in directions[x][y]:
                        queue.append((nx, ny))
        return False

    t, N, M = map(int, stdin.readline().split())
    grid = [list(stdin.readline().strip()) for _ in range(N)]
    S_x, S_y = None, None
    directions = [[''] * M for _ in range(N)]

    for x in range(N):
        for y in range(M):
            if grid[x][y] == 'S':
                S_x, S_y = x, y
            elif grid[x][y] in ['U', 'D', 'L', 'R']:
                directions[x][y] = {'U': (0, -1), 'D': (0, 1), 'L': (-1, 0), 'R': (1, 0)}[grid[x][y]]

    if S_x is None or S_y is None:
        return "NOT POSSIBLE"

    result = float('inf')
    for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
        nx, ny = S_x + dx, S_y + dy
        if is_valid(nx, ny) and (dx, dy) not in directions[S_x][S_y]:
