        if start_index >= len(expr):
            distinct_integers.add(expr[0])
            return

        if isinstance(expr[start_index], int):
            evaluate(expr, start_index + 1)
        else:
            count = count_strings([str(x) for x in expr[start_index - 1::2] if isinstance(x, int)])
            for i in range(count):
                for j in range(i + 1, count):
                    new_expr = expr.copy()
                    new_expr[start_index - 1] = str(new_expr[start_index - 1]) + str(new_expr[start_index + 1])
                    del new_expr[start_index]
                    evaluate(new_expr, start_index - 1)

    evaluate(parsed_input, 1)
    return len(distinct_integers)

