    def find_min_distance(start, visited, distance, graph):
        min_distance = float('inf')
        next_node = -1
        for node in graph[start]:
            if not visited[node]:
                d = distance[start] + graph[start][node]
                if d < min_distance:
                    min_distance = d
                    next_node = node
        return next_node, min_distance

    def dijkstra(graph, start):
        visited = [False] * len(graph)
        distance = [float('inf')] * len(graph)
        distance[start] = 0

        for _ in range(len(graph) - 1):
            min_node, min_distance = find_min_distance(start, visited, distance, graph)
            if min_node == -1:
                break
            visited[min_node] = True
            for neighbor in graph[min_node]:
                if not visited[neighbor]:
                    dist = min_distance + graph[min_node][neighbor]
                    if dist < distance[neighbor]:
                        distance[neighbor] = dist

        return distance

    def reconstruct_map(table, start):
        n = len(table)
        graph = {i: {} for i in range(n)}
        for i in range(n):
            for j in range(n):
                if i != j:
                    graph[i][j] = table[i][j]

        distance = dijkstra(graph, start)

        roads = []
        for i in range(1, n):
            if distance[i] != float('inf'):
                roads.append((start, i))

        return roads

    n = int(stdin.readline())
    table = [list(map(int, stdin.readline().split())) for _ in range(n)]

