    parent = list(range(n + 1))

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            parent[root_x] = root_y

    def get_size(x):
        root = find(x)
        return -parent[root]

    for i in range(1, q + 1):
        line = lines[i].split()
        if line[0] == 't':
