        languages[name] = set([primary_lang] + secondary_langs)

    def can_converse(char1, char2):
        if char1 == char2:
            return True
        common_langs = languages[char1].intersection(languages[char2])
        if common_langs:
            return True
        for lang1 in languages[char1]:
            for lang2 in languages[char2]:
                if lang1 in languages[char2] or lang2 in languages[char1]:
                    return True
        return False

    remaining_chars = set(languages.keys())
    min_size = num_chars
    for char1 in languages.keys():
        remaining_chars.remove(char1)
        for char2 in languages.keys():
            if char2 in remaining_chars and not can_converse(char1, char2):
                remaining_chars.remove(char2)
        min_size = min(min_size, len(remaining_chars))
        remaining_chars = set(languages.keys())

    return str(min_size)

